import React, {useState} from 'react';
import axios from 'axios';

const AddFacultyForm = ({onFacultyAdded}) => {
    const [name, setName] = useState('');
    const [department, setDepartment] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [qualifications, setQualifications] = useState('');
    const [unavailability, setUnavailability] = useState('');
    const [maxHoursPerWeek, setMaxHoursPerWeek] = useState(10);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if(!name || !department || !email) {
            alert("Name, Department and Email are required.");
            return;
        }

        try {
            const newFaculty = {
                name,
                department,
                email,
                phone,
                qualifications: qualifications.split(',').map(q => q.trim()),
                unavailability: unavailability.split(',').map(u => u.trim()),
                maxHoursPerWeek
            };
            await axios.post('http://localhost:5000/api/faculties', newFaculty);
            onFacultyAdded();
            setName('');
            setDepartment('');
            setEmail('');
            setPhone('');
            setQualifications('');
            setUnavailability('');
            setMaxHoursPerWeek(10);
        } catch (error) {
            console.error("Error adding faculty:", error);
            alert("Failed to add faculty. Please try again.");
        }
    };
    return (
        <form onSubmit={handleSubmit}>
            <h3>Add New Faculty</h3>
            <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
            <input type="text" placeholder="Department" value={department} onChange={(e) => setDepartment(e.target.value)} />
            <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <input type="text" placeholder="Phone" value={phone} onChange={(e) => setPhone(e.target.value)} />
            <input type="text" placeholder="Qualifications (comma separated)" value={qualifications} onChange={(e) => setQualifications(e.target.value)} />
            <input type="text" placeholder="Unavailability (comma separated)" value={unavailability} onChange={(e) => setUnavailability(e.target.value)} />
            <input type="number" placeholder="Max Hours/Week" value={maxHoursPerWeek} onChange={(e) => setMaxHoursPerWeek(e.target.value)} />
            <button type="submit">Add Faculty</button>
        </form>
    );      
};

export default AddFacultyForm;
