// src/pages/EditFacultyPage.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EditFacultyPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [facultyData, setFacultyData] = useState({
    name: '',
    department: '',
    email: '',
    phone: '',
    qualifications: '',
    unavailability: '',
    maxHoursPerWeek: 10
  });

  useEffect(() => {
    const fetchFaculty = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/faculties/${id}`);
        const data = response.data;

        const updatedData = {
          ...data,
          qualifications: data.qualifications ? data.qualifications.map(q => q.name).join(', ') : '',
          unavailability: data.unavailability ? data.unavailability.join(', ') : ''
        };

        setFacultyData(updatedData);
      } catch (error) {
        console.error("Error fetching faculty data:", error);
      }
    };

    fetchFaculty();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFacultyData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const dataToSubmit = {
      ...facultyData,
      qualifications: facultyData.qualifications.split(',').map(item => item.trim()),
      unavailability: facultyData.unavailability.split(',').map(item => item.trim())
    };

    try {
      await axios.put(`http://localhost:5000/api/faculties/${id}`, dataToSubmit);
      navigate('/faculties');
    } catch (error) {
      console.error("Error updating faculty:", error);
      alert("Failed to update faculty. Please check the console for details.");
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Edit Faculty</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '10px' }}>
          <label>Name: </label>
          <input type="text" name="name" value={facultyData.name} onChange={handleChange} />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>Department: </label>
          <input type="text" name="department" value={facultyData.department} onChange={handleChange} />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>Email: </label>
          <input type="email" name="email" value={facultyData.email} onChange={handleChange} />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>Phone: </label>
          <input type="text" name="phone" value={facultyData.phone} onChange={handleChange} />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>Qualifications (comma-separated): </label>
          <input type="text" name="qualifications" value={facultyData.qualifications} onChange={handleChange} />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>Unavailability (comma-separated): </label>
          <input type="text" name="unavailability" value={facultyData.unavailability} onChange={handleChange} />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>Max Hours/Week: </label>
          <input type="number" name="maxHoursPerWeek" value={facultyData.maxHoursPerWeek} onChange={handleChange} />
        </div>
        <button type="submit">Update Faculty</button>
      </form>
    </div>
  );
};

export default EditFacultyPage;
