import React, {useState} from 'react';
import axios from 'axios';
import './AddFacultyForm.css';

const AddFacultyForm = ({onFacultyAdded}) => {
    const [name, setName] = useState('');
    const [department, setDepartment] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [qualifications, setQualifications] = useState('');
    const [unavailability, setUnavailability] = useState('');
    const [maxHoursPerWeek, setMaxHoursPerWeek] = useState(10);
    const [isSubmitting, setIsSubmitting] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if(!name || !department || !email) {
            alert("Name, Department and Email are required.");
            return;
        }

        setIsSubmitting(true);
        try {
            const newFaculty = {
                name,
                department,
                email,
                phone,
                qualifications: qualifications.split(',').map(q => q.trim()),
                unavailability: unavailability.split(',').map(u => u.trim()),
                maxHoursPerWeek: parseInt(maxHoursPerWeek)
            };
            await axios.post('http://localhost:5000/api/faculties', newFaculty);
            onFacultyAdded();
            
            // Reset form
            setName('');
            setDepartment('');
            setEmail('');
            setPhone('');
            setQualifications('');
            setUnavailability('');
            setMaxHoursPerWeek(10);
            
            // Show success message
            alert("Faculty added successfully!");
        } catch (error) {
            console.error("Error adding faculty:", error);
            alert("Failed to add faculty. Please try again.");
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="add-faculty-container">
            <div className="form-header">
                <h2 className="form-title">
                    <span className="form-icon">üë®‚Äçüè´</span>
                    Add New Faculty
                </h2>
                <p className="form-subtitle">Fill in the details to add a new faculty member</p>
            </div>

            <form onSubmit={handleSubmit} className="add-faculty-form">
                <div className="form-row">
                    <div className="form-group">
                        <label htmlFor="name" className="form-label">
                            Full Name <span className="required">*</span>
                        </label>
                        <div className="input-wrapper">
                            <span className="input-icon">üë§</span>
                            <input 
                                id="name"
                                type="text" 
                                placeholder="Enter full name" 
                                value={name} 
                                onChange={(e) => setName(e.target.value)}
                                className="form-input"
                                required
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="department" className="form-label">
                            Department <span className="required">*</span>
                        </label>
                        <div className="input-wrapper">
                            <span className="input-icon">üè¢</span>
                            <input 
                                id="department"
                                type="text" 
                                placeholder="Enter department" 
                                value={department} 
                                onChange={(e) => setDepartment(e.target.value)}
                                className="form-input"
                                required
                            />
                        </div>
                    </div>
                </div>

                <div className="form-row">
                    <div className="form-group">
                        <label htmlFor="email" className="form-label">
                            Email Address <span className="required">*</span>
                        </label>
                        <div className="input-wrapper">
                            <span className="input-icon">üìß</span>
                            <input 
                                id="email"
                                type="email" 
                                placeholder="Enter email address" 
                                value={email} 
                                onChange={(e) => setEmail(e.target.value)}
                                className="form-input"
                                required
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="phone" className="form-label">Phone Number</label>
                        <div className="input-wrapper">
                            <span className="input-icon">üì±</span>
                            <input 
                                id="phone"
                                type="tel" 
                                placeholder="Enter phone number" 
                                value={phone} 
                                onChange={(e) => setPhone(e.target.value)}
                                className="form-input"
                            />
                        </div>
                    </div>
                </div>

                <div className="form-group">
                    <label htmlFor="qualifications" className="form-label">Qualifications</label>
                    <div className="input-wrapper">
                        <span className="input-icon">üéì</span>
                        <input 
                            id="qualifications"
                            type="text" 
                            placeholder="Enter qualifications (comma separated)" 
                            value={qualifications} 
                            onChange={(e) => setQualifications(e.target.value)}
                            className="form-input"
                        />
                    </div>
                    <small className="input-help">Example: PhD Computer Science, MSc Mathematics</small>
                </div>

                <div className="form-group">
                    <label htmlFor="unavailability" className="form-label">Unavailability</label>
                    <div className="input-wrapper">
                        <span className="input-icon">‚è∞</span>
                        <input 
                            id="unavailability"
                            type="text" 
                            placeholder="Enter unavailable times (comma separated)" 
                            value={unavailability} 
                            onChange={(e) => setUnavailability(e.target.value)}
                            className="form-input"
                        />
                    </div>
                    <small className="input-help">Example: Monday 9-11 AM, Wednesday 2-4 PM</small>
                </div>

                <div className="form-group">
                    <label htmlFor="maxHours" className="form-label">Maximum Hours Per Week</label>
                    <div className="input-wrapper">
                        <span className="input-icon">üïê</span>
                        <input 
                            id="maxHours"
                            type="number" 
                            min="1"
                            max="40"
                            placeholder="Max hours per week" 
                            value={maxHoursPerWeek} 
                            onChange={(e) => setMaxHoursPerWeek(e.target.value)}
                            className="form-input"
                        />
                    </div>
                    <small className="input-help">Enter a value between 1-40 hours</small>
                </div>

                <div className="form-actions">
                    <button 
                        type="submit" 
                        className={`submit-btn ${isSubmitting ? 'submitting' : ''}`}
                        disabled={isSubmitting}
                    >
                        {isSubmitting ? (
                            <>
                                <span className="spinner"></span>
                                Adding Faculty...
                            </>
                        ) : (
                            <>
                                <span className="btn-icon">‚ûï</span>
                                Add Faculty
                            </>
                        )}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default AddFacultyForm;
